import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Category, categories} from '../categories';
import { Product, products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {

  product: Product | undefined;
  category: Category | undefined;

  constructor(
    private _activatedRoute: ActivatedRoute
  ) {
  }

  removeProduct(ind : number){
    // @ts-ignore
    this.category.items = this.category.items.filter((x) => x.id !== ind);
  }
  ngOnInit() {
    const routeParams = this.route.snapshot.paramMap;
    const categoryIdFromRoute = Number(routeParams.get('categoryId'));

    this.category = categories.find(category => category.id === categoryIdFromRoute);
  }

  getCategory() {
    const routeParams = this._activatedRoute.snapshot.paramMap;
    const productCategoryFromRoute = String(routeParams.get('categories'));
    // Find the product that correspond with the id provided in route.
    this.category = categories.find(category => category.name === productCategoryFromRoute);
  }
  share() {
    window.alert('The product has been shared!');
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
}


